package programming_elements;

public class Modulo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int pen = 14;
		int student = 3;
		int pen_student = pen/student;
		int remaining_pen = pen%student;
		System.out.println("Then pen per student is "+pen_student+" and the remaining pen not distributed is "+remaining_pen);
	}

}
